policies.inbound:






    @(context.Subscription.Name)


<set-variable name="InitiatingState" value="@{
    string pattern = @"^(?[a-z]{2})-";
    var matches = Regex.Match(context.Subscription.Name.ToLower(), pattern);
    return matches.Groups["state"].Value;
}" />

@{
    const string metaKey = "meta";
    JObject inBody = context.Request.Body.As();

    if (!inBody.ContainsKey(metaKey))
    {
        inBody[metaKey] = new JObject();
    }

    JToken meta = inBody.GetValue(metaKey);
    meta["initiating_state"] = context.Variables.GetValueOrDefault("InitiatingState");

    return inBody.ToString();
}
